import { useEffect } from 'react';
import { useState } from 'react';
import './App.scss';
import Create from './Components/Create';
import List from './Components/List';
import axios from 'axios';
import Edit from './Components/Edit';
import Msg from './Components/Msg';

function App() {
  const [animals, setAnimals] = useState(null);
  const [lastUpdate, setLastUpdate] = useState(Date.now());
  useEffect(() => {
    axios.get('http://animals.zoo/react/list')
      .then(res => setAnimals(res.data));
  }, [lastUpdate]);
  const [createData, setCreateData] = useState(null);
  const [deleteData, setDeleteData] = useState(null);
  const [modalData, setModalData] = useState(null);
  const [editData, setEditData] = useState(null);
  const [msg, setMsg] = useState('null');
  useEffect(() => {
    if (null == createData) {
      return;
    }
    axios.post('http://animals.zoo/react/list', createData)
      .then(res =>
        setLastUpdate(Date.now()));

  }, [createData]);
  useEffect(() => {
    if (null == deleteData) {
      return;
    }
    axios.delete('http://animals.zoo/react/list/' + deleteData.id)
      .then(res => setLastUpdate(Date.now()));

  }, [deleteData]);
  useEffect(() => {
    if (null == editData) {
      return;
    }
    axios.put('http://animals.zoo/react/list/' + editData.id, editData)
      .then(res =>
        setLastUpdate(Date.now()));

  }, [editData]);
  return (
    <>
      <div className="container">
        <div className="row">
          <div className="col-5">
            <Create setCreateData={setCreateData} />
          </div>
          <div className="col-7">
            <List list={animals} setDeleteData={setDeleteData} setModalData={setModalData} />
          </div>
        </div>
      </div>
      <Edit modalData={modalData} setModalData={setModalData} setEditData={setEditData} />
      <Msg msg={msg}/>
    </>
  );
}

export default App;
